const segments = prompt
  .split(/(?:\band\b|\balso\b|[,.?!;])+/i)
  .map(s => s.trim())
  .filter(s => s.length > 3); // Filter out noise

return segments.map((p, idx) => ({
  json: {
    prompt: p,
    promptIndex: idx,
    sessionId,
    timestamp,
